query:
query {
  repository(owner: "airbytehq", name: "airbyte") {
    name
    owner {
      login
    }
    projectsV2(first: 100, orderBy: {field: UPDATED_AT, direction: ASC}) {
      nodes {
        closed
        created_at: createdAt
        closed_at: closedAt
        updated_at: updatedAt
        creator: creator {
          avatarUrl
          login
          resourcePath
          url
        }
        node_id: id
        id: databaseId
        number
        public
        readme: readme
        short_description: shortDescription
        template
        title: title
        url: url
        viewerCanClose
        viewerCanReopen
        viewerCanUpdate
        owner {
          id: id
        }
      }
      pageInfo {
        hasNextPage
        endCursor
      }
    }
  }
}
