query:
query {
  repository(owner: "airbytehq", name: "airbyte") {
    name
    owner {
      login
    }
    pullRequests(first: 10, orderBy: {field: UPDATED_AT, direction: ASC}) {
      nodes {
        node_id: id
        id: databaseId
        number
        updated_at: updatedAt
        changed_files: changedFiles
        deletions
        additions
        merged
        mergeable
        can_be_rebased: canBeRebased
        maintainer_can_modify: maintainerCanModify
        merge_state_status: mergeStateStatus
        comments {
          totalCount
        }
        commits {
          totalCount
        }
        review_comments: reviews(first: 100) {
          totalCount
          nodes {
            comments {
              totalCount
            }
          }
        }
        merged_by: mergedBy {
          __typename
          ... on User {
            node_id: id
            id: databaseId
            login
            avatar_url: avatarUrl
            html_url: url
            site_admin: isSiteAdmin
          }
        }
      }
      pageInfo {
        hasNextPage
        endCursor
      }
    }
  }
}
